mapper.select:
select

from arius_es_user
where is_active = 1
and id = #{id}
and query_threshold = #{queryThreshold}
and cluster = #{cluster}
and search_type = #{searchType}
and is_root = #{isRoot}
and project_id = #{projectId}
 and responsible = #{responsible}
order by id desc

select

from arius_es_user
where is_active = 1 and id = #{esUser}

select

from arius_es_user
where is_active = 1 and project_id = #{projectId}

mapper.update:
update arius_es_user

    ,project_id = #{projectId}
    ,query_threshold = #{queryThreshold}
    ,verify_code = #{verifyCode}
    ,cluster = #{cluster}
    ,search_type = #{searchType}
    ,memo = #{memo}
    ,is_root = #{isRoot}
    ,responsible = #{responsible}
    ,is_default_display = #{defaultDisplay}


where id = #{id} and is_active = 1

update arius_es_user
set is_active = 0
where id = #{esUser}

update arius_es_user
set is_active = 0
where id = #{projectId}
