mapper.select:
select

from operate_record


		and id = #{param.id}


		and module_id = #{param.moduleId}


		and operate_id = #{param.operateId}


		and trigger_way_id = #{param.triggerWayId}


		and operate_time >= #{param.beginTime}


		and operate_time <= #{param.endTime}


		and project_name = #{param.projectName}
			 OR project_name LIKE CONCAT(#{param.projectName}, '%')
			 OR project_name LIKE CONCAT('%', #{param.projectName})
			 OR project_name LIKE CONCAT('%', #{param.projectName}, '%')


		and user_operation = #{param.userOperation}


		and content = #{param.content}
			 OR content LIKE CONCAT(#{param.content}, '%')
			 OR content LIKE CONCAT('%', #{param.content})
			 OR content LIKE CONCAT('%', #{param.content}, '%')


order by id desc
limit #{param.from},#{param.size}

select count(id)
from operate_record


		and id = #{param.id}


		and module_id = #{param.moduleId}


		and operate_id = #{param.operateId}


		and trigger_way_id = #{param.triggerWayId}


		and operate_time >= #{param.beginTime}


		and operate_time <= #{param.endTime}


		and project_name = #{param.projectName}
			 OR project_name LIKE CONCAT(#{param.projectName}, '%')
			 OR project_name LIKE CONCAT('%', #{param.projectName})
			 OR project_name LIKE CONCAT('%', #{param.projectName}, '%')


		and user_operation = #{param.userOperation}


		and content = #{param.content}
			 OR content LIKE CONCAT(#{param.content}, '%')
			 OR content LIKE CONCAT('%', #{param.content})
			 OR content LIKE CONCAT('%', #{param.content}, '%')

mapper.update:
delete
from operate_record
where module_id = #{moduleId}
	and id < #{id}
