INSERT INTO `arius_config_info` VALUES (1681, 'arius.common.group', 'super_app.default.dsl.command', '#获取节点状态\nGET _nodes/stats\n\n#获取集群信息\nGET _cluster/stats\n\n#获取集群健康信息\nGET _cluster/health?v\n\n#查看当前集群的热点线程\nGET _nodes/hot_threads\n\n#查看当前集群运行中的任务信息\nGET _tasks?actions=*&detailed\n\n#shard分配说明，会在分片未分配的事后去通过这个命令查看下具体原因\nGET /_cluster/allocation/explain\n\n#异常shard分配重试，当集群red有shard未分配的情况下会通过这个命令来重试分配\nPOST /_cluster/reroute?retry_failed=true\n\n#清除fielddata内存，当集群因为fileddata太大导致熔断或占用很多内存，可以通过此命令释放内存\nPOST _cache/clear?fielddata=true\n', 1, -1, 1, '超级应用默认就有的命令', '2022-09-20 10:26:08', '2022-09-26 11:34:14', '2022-09-20 10:26:08');
