                concat('[{\"firstUserConfigType\":\"searchQuery\",\"projectId\":',t2.project_id,',\"secondUserConfigType\":\"searchTemplate\",\"userConfigTypes\":[\"totalCostAvg\",\"totalShardsAvg\"],\"userName\":\"',t1.user_name,'\"}]') as config_info
